import { ChatMessageProps } from './types';

export const ChatMessage: React.FC<ChatMessageProps> = ({ message }) => {
  console.log(message);
  return (
    <div
      data-testid="chat-message"
      className="flex flex-col items-start justify-start bg-red-500 rounded-lg border-none my-6 mx-10 p-2 px-5 flex items-end blue-glassmorphism text-white"
    >
      <div>
        <p className="text-bold text-sm text-black">{message?.name}</p>
        <p>{message?.time}</p>
      </div>

      <p>{message?.message}</p>
    </div>
  );
};
