import { ChatProps } from './types';
import { io, Socket } from 'socket.io-client';
import { useEffect, useState } from 'react';

export const Chat: React.FC<ChatProps> = () => {
  const ENDPOINT = 'http://localhost:4000';

  const [message, setMessage] = useState('initialState');
  const socket = io(ENDPOINT);

  useEffect(() => {
    socket.emit('yo', 'yo');

    socket.on('message', (data) => console.log(data));
  }, [socket]);

  return (
    <div data-testid="chat" className="flex flex-col">
      <div className="flex w-screen h-screen">
        <div className="w-[15vw] h-full bg-gray-300"></div>
        <div className="w-full h-full bg-blue-300 flex flex-col">
          <div className="flex items-center justify-start mt-auto mb-5 ml-10">
            <input className="mr-10 w-100" />
            <button className="rounded-md bg-blue-500">Send</button>
          </div>
        </div>
        {/* <button onClick={() => socket.emit('log', 'message')}>click</button> */}
      </div>
    </div>
  );
};
